function main({json_data, time_string}){
    // 去掉外部的反引号和换行符
	let jsonDataStart = json_data.indexOf('{');
	let jsonDataEnd = json_data.lastIndexOf('}');
	let jsonData = json_data.substring(jsonDataStart, jsonDataEnd + 1);

	// 解析JSON字符串
	let jsonObj = JSON.parse(jsonData);

	let todoItem = jsonObj.todo_item;
	let todoTime = jsonObj.todo_time;

    // 使用正则表达式匹配日期时间部分
    let dateTimeRegex = /\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}/;
    let dateTimeMatch = time_string.match(dateTimeRegex);
    return {
        todo_item: todoItem,
        todo_time: todoTime,
        now_time : dateTimeMatch[0]
    }
}

