function main({ tasks }) {
  if (!tasks || tasks.length === 0) {
    throw new Error("没有找到任何任务");
  }

  // 找到最新任务
  const latestTask = tasks.reduce((latest, current) => 
    new Date(current.created_time) > new Date(latest.created_time) ? current : latest
  );

  // 直接返回ID，FastGPT会自动作为输出
  return latestTask.id;
}

